import React, { useState } from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { loginUser } from '../../services/api';
import loginBg from '../../assets/login-bg.png'; // Make sure to add your image

const Login = () => {
  const [form, setForm] = useState({ email: '', motdepasse: '' });
  const [error, setError] = useState('');
  const history = useHistory();
  const location = useLocation();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(form);
      if (response.data) {
        localStorage.setItem('token', response.data.accessToken);
        localStorage.setItem('user', JSON.stringify(response.data));
        history.push('/dashboard');
      }
    } catch (err) {
      setError('Email ou mot de passe incorrect');
    }
  };

  return (
    <div className="login-container">
      <div className="login-content">
        <div className="login-left">
          <img src={loginBg} alt="InternMatch" className="login-image" />
        </div>

        <div className="login-right">
          <div className="login-form-container">
            <h2>Connexion</h2>
            <p className="login-subtitle">
              Bienvenue sur InternMatch. Connectez-vous pour continuer.
            </p>

            {error && <div className="error-message">{error}</div>}

            <form onSubmit={handleSubmit} className="login-form">
              <div className="form-group">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  value={form.email}
                  onChange={(e) => setForm({...form, email: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Mot de passe</label>
                <input
                  type="password"
                  className="form-control"
                  value={form.motdepasse}
                  onChange={(e) => setForm({...form, motdepasse: e.target.value})}
                  required
                />
              </div>

              <button type="submit" className="btn btn-primary btn-block">
                Se connecter
              </button>
            </form>

            <div className="login-footer">
              <p>Pas encore de compte ?</p>
              <Link to="/register" className="btn btn-secondary btn-block">
                Créer un compte
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
