import api from './api';

export const getAllStages = async () => {
    const response = await api.get('/stages');
    return response.data;
};

export const getEntrepriseStages = async () => {
    try {
        const response = await api.get('/api/entreprise/stages');
        return response.data;
    } catch (error) {
        console.error('Error fetching enterprise stages:', error);
        throw error;
    }
};

export const createStage = async (stageData) => {
    const response = await api.post('/api/entreprise/stages', stageData);
    return response.data;
};

export const postulerStage = async (stageId) => {
    const response = await api.post(`/stages/${stageId}/postuler`);
    return response.data;
};

export const getStageStats = async () => {
    const response = await api.get('/admin/stages/stats');
    return response.data;
};

export const getCandidatures = async () => {
    try {
        const response = await api.get('/api/entreprise/candidatures');
        return response.data;
    } catch (error) {
        console.error('Error fetching candidatures:', error);
        throw error;
    }
};

export const traiterCandidature = async (candidatureId, data) => {
    try {
        const response = await api.put(`/api/entreprise/candidatures/${candidatureId}/traiter`, data);
        return response.data;
    } catch (error) {
        console.error('Error processing candidature:', error);
        throw error;
    }
};
