import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

const getToken = () => localStorage.getItem('token');

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Auth
export const registerUser = (data) => api.post('/auth/register', data);
export const loginUser = async (credentials) => {
  try {
    const response = await api.post('/auth/login', credentials);

    // Validate response structure
    const { data } = response;
    if (!data || !data.accessToken || !data.role) {
      throw new Error('Invalid response format from server');
    }

    return response;
  } catch (error) {
    console.error('Login API Error:', {
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    throw error;
  }
};
export const registerAdmin = async (adminData) => {
  const response = await api.post('/auth/admin/register', adminData);
  return response.data;
};
// Etudiant
export const getEtudiantProfile = () => api.get('/etudiant/profile');
export const updateEtudiantProfile = (data) => api.put('/etudiant/profile', data);
export const getEtudiantCandidatures = async () => {
  const response = await api.get('/etudiant/candidatures');
  return response.data;
};
export const postulerStage = async (data) => {
  const response = await api.post('/etudiant/candidatures', data);
  return response.data;
};
export const getEtudiantStages = () => api.get('/etudiant/stages');

// Entreprise
export const getEntrepriseProfile = () => api.get('/entreprise/profile');
export const updateEntrepriseProfile = (data) => api.put('/entreprise/profile', data);
export const getEntrepriseStages = async () => {
  try {
    const response = await api.get('/entreprise/stages');
    // Ensure we return an array
    return {
      data: Array.isArray(response.data) ? response.data : []
    };
  } catch (error) {
    console.error('API Error:', error);
    throw error;
  }
};
export const createStage = (data) => api.post('/entreprise/stages', data);
export const updateStage = (id, data) => api.put(`/entreprise/stages/${id}`, data);
export const getStageCandidatures = (stageId) => api.get(`/entreprise/stages/${stageId}/candidatures`);
export const traiterCandidature = async ({ candidatureId, status, commentaire }) => {
  const response = await api.put(`/candidatures/${candidatureId}/traiter`, {
    status,
    commentaire
  });
  return response.data;
