import api from './api';

export const traiterCandidature = async (candidatureId, data) => {
    const response = await api.put(`/candidatures/${candidatureId}/traiter`, data);
    return response.data;
};

export const getCandidatures = async () => {
    const response = await api.get('/candidatures');
    return response.data;
};

export const getEtudiantCandidatures = async () => {
    const response = await api.get('/candidatures/etudiant');
    return response.data;
};

export const updateCandidature = async (id, data) => {
    const response = await api.put(`/candidatures/${id}`, data);
    return response.data;
};
