import React, { useEffect, useState } from 'react';
import { getEntrepriseStages, getStageCandidatures, traiterCandidature } from '../services/api';

const statusLabels = {
  en_attente: 'En attente',
  validé: 'Validé',
  refusé: 'Refusé'
};

const EntrepriseCandidatures = () => {
  const [stages, setStages] = useState([]);
  const [selectedStage, setSelectedStage] = useState(null);
  const [candidatures, setCandidatures] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [comment, setComment] = useState('');
  const [selectedCandidature, setSelectedCandidature] = useState(null);

  useEffect(() => {
    let mounted = true;

    const fetchStages = async () => {
      setIsLoading(true);
      try {
        const response = await getEntrepriseStages();
        if (mounted && Array.isArray(response.data)) {
          setStages(response.data);
        }
      } catch (err) {
        if (mounted) {
          console.error('Error fetching stages:', err);
          setError(err.response?.data?.message || "Erreur lors du chargement des stages");
        }
      } finally {
        if (mounted) {
          setIsLoading(false);
        }
      }
    };

    fetchStages();

    return () => {
      mounted = false;
    };
  }, []);

  const handleStageSelect = async (stageId) => {
    if (!stageId) return;
    
    setSelectedStage(stageId);
    setCandidatures([]);
    setIsLoading(true);
    try {
      const response = await getStageCandidatures(stageId);
      if (Array.isArray(response.data)) {
        setCandidatures(response.data);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Erreur lors du chargement des candidatures");
    } finally {
      setIsLoading(false);
    }
  };

  const handleTraiter = async (candidatureId, status) => {
    if (!candidatureId || !status) return;

    setIsLoading(true);
    try {
      await traiterCandidature({ 
        candidatureId, 
        status,
        commentaire: comment 
      });
      setComment('');
      setSelectedCandidature(null);
      if (selectedStage) {
        await handleStageSelect(selectedStage);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Erreur lors du traitement");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="candidatures-container">
      <h2>Gestion des Candidatures</h2>
      {error && <div className="error-message">{error}</div>}
      
      <div className="stage-selector">
        <label>Sélectionner un stage :</label>
        <select 
          onChange={(e) => handleStageSelect(e.target.value)} 
          value={selectedStage || ''}
          className="form-control"
        >
          <option value="">-- Sélectionner un stage --</option>
          {Array.isArray(stages) && stages.map(stage => (
            <option key={stage.id} value={stage.id}>
              {stage.titre}
            </option>
          ))}
        </select>
      </div>

      {isLoading ? (
        <div className="loading">Chargement...</div>
      ) : (
        <>
          {candidatures.length > 0 ? (
            <div className="candidatures-list">
              {candidatures.map(candidature => (
                <div key={candidature.id} className="candidature-card">
                  <div className="candidature-header">
                    <h3>{candidature.etudiantNom} {candidature.etudiantPrenom}</h3>
                    <span className={`status-badge ${candidature.status}`}>
                      {statusLabels[candidature.status]}
                    </span>
                  </div>
                  
                  <div className="candidature-details">
                    <p><strong>Date de candidature:</strong> {new Date(candidature.datePostulation).toLocaleDateString()}</p>
                    {candidature.commentaire && (
                      <p><strong>Commentaire:</strong> {candidature.commentaire}</p>
                    )}
                  </div>

                  {candidature.status === 'en_attente' && (
                    <div className="candidature-actions">
                      {selectedCandidature === candidature.id ? (
                        <div className="comment-section">
                          <textarea
                            className="form-control"
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                            placeholder="Ajouter un commentaire..."
                          />
                          <div className="action-buttons">
                            <button 
                              className="btn btn-success"
                              onClick={() => handleTraiter(candidature.id, 'validé')}
                            >
                              Valider
                            </button>
                            <button 
                              className="btn btn-danger"
                              onClick={() => handleTraiter(candidature.id, 'refusé')}
                            >
                              Refuser
                            </button>
                            <button 
                              className="btn btn-secondary"
                              onClick={() => {
                                setSelectedCandidature(null);
                                setComment('');
                              }}
                            >
                              Annuler
                            </button>
                          </div>
                        </div>
                      ) : (
                        <button 
                          className="btn btn-primary"
                          onClick={() => setSelectedCandidature(candidature.id)}
                        >
                          Traiter la candidature
                        </button>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            selectedStage && <p>Aucune candidature pour ce stage.</p>
          )}
        </>
      )}
    </div>
  );
};

export default EntrepriseCandidatures;
