import React, { useEffect, useState } from 'react';
import { getAllStages, postulerStage } from '../services/api';

const InternshipList = () => {
  const [internships, setInternships] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchInternships();
  }, []);

  const fetchInternships = async () => {
    setIsLoading(true);
    try {
      const data = await getAllStages();
      setInternships(data);
    } catch (err) {
      setError(err.response?.data?.message || "Erreur de chargement des stages");
    } finally {
      setIsLoading(false);
    }
  };

  const handleApply = async (stageId) => {
    try {
      await postulerStage({ stageId });
      setSuccessMessage('Candidature envoyée avec succès !');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError(err.response?.data?.message || "Erreur lors de la candidature");
    }
  };

  return (
    <div className="internship-list">
      <h2>Stages Disponibles</h2>

      {successMessage && <div className="success-message">{successMessage}</div>}
      {error && <div className="error-message">{error}</div>}

      {isLoading ? (
        <div className="loading">Chargement...</div>
      ) : (
        <div className="internships-grid">
          {internships.length === 0 ? (
            <p>Aucun stage disponible pour le moment.</p>
          ) : (
            internships.map(stage => (
              <div key={stage.id} className="internship-card">
                <h3>{stage.titre}</h3>
                <div className="internship-details">
                  <p><strong>Entreprise:</strong> {stage.entrepriseNom}</p>
                  <p><strong>Durée:</strong> {stage.duree}</p>
                  <p><strong>Date début:</strong> {new Date(stage.dateDebut).toLocaleDateString()}</p>
                  <p><strong>Date fin:</strong> {new Date(stage.dateFin).toLocaleDateString()}</p>
                  <p className="description">{stage.description}</p>
                </div>
                <button 
                  onClick={() => handleApply(stage.id)}
                  className="btn btn-primary"
                >
                  Postuler
                </button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default InternshipList;
