import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { loginUser } from '../../services/authService';
import leftImage from '../../assets/im1.png';
import rightImage from '../../assets/im2.png';

const Login = () => {
    const [credentials, setCredentials] = useState({
        email: '',
        motdepasse: ''
    });
    const [error, setError] = useState('');
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await loginUser(credentials);

            // Redirect based on user role
            if (response.user.role === 'admin') {
                history.push('/admin-dashboard');
            } else if (response.user.role === 'entreprise') {
                history.push('/entreprise-dashboard');
            } else {
                history.push('/dashboard');
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Erreur de connexion');
        }
    };

    return (
        <div className="login-wrapper">
            <div className="login-container">
                <div className="login-images">
                    <div className="image-left">
                        <img src={leftImage} alt="Welcome to InternMatch" />
                    </div>
                    <div className="image-right">
                        <img src={rightImage} alt="Connect with opportunities" />
                    </div>
                </div>

                <div className="login-box">
                    <div className="login-form-container">
                        <div className="login-header">
                            <h1 className="welcome-title">BIENVENUE À</h1>
                            <div className="app-logo">
                                <span className="logo-text">InternMatch</span>
                            </div>
                        </div>

                        <div className="login-form-wrapper">
                            <h2 className="login-title">Connexion</h2>

                            {error && <div className="error-message">{error}</div>}

                            <form onSubmit={handleSubmit} className="login-form">
                                <div className="form-group">
                                    <input
                                        type="email"
                                        className="form-control"
                                        placeholder="Adresse email"
                                        value={credentials.email}
                                        onChange={(e) => setCredentials({...credentials, email: e.target.value})}
                                        required
                                    />
                                </div>

                                <div className="form-group">
                                    <input
                                        type="password"
                                        className="form-control"
                                        placeholder="Mot de passe"
                                        value={credentials.motdepasse}
                                        onChange={(e) => setCredentials({...credentials, motdepasse: e.target.value})}
                                        required
                                    />
                                </div>

                                <button type="submit" className="btn btn-primary btn-block login-submit-btn">
                                    Se connecter
                                </button>
                            </form>

                            <div className="login-links">
                                <Link to="/forgot-password" className="forgot-password-link">
                                    Mot de passe oublié ?
                                </Link>
                            </div>

                            <div className="login-footer">
                                <p className="signup-text">Pas encore de compte ?</p>
                                <Link to="/register" className="btn btn-secondary btn-block signup-btn">
                                    Créer un compte
                                </Link>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
