import React, { useState, useEffect } from 'react';
import { getEntrepriseStages, createStage, updateStage } from '../services/api';

const EntrepriseStages = () => {
  const [stages, setStages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    titre: '',
    description: '',
    duree: '',
    dateDebut: '',
    dateFin: ''
  });

  useEffect(() => {
    fetchStages();
  }, []);

  const fetchStages = async () => {
    setIsLoading(true);
    try {
      const data = await getEntrepriseStages();
      setStages(data);
    } catch (err) {
      setError(err.response?.data?.message || "Erreur lors du chargement des stages");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createStage(formData);
      setShowForm(false);
      setFormData({
        titre: '',
        description: '',
        duree: '',
        dateDebut: '',
        dateFin: ''
      });
      fetchStages();
    } catch (err) {
      setError(err.response?.data?.message || "Erreur lors de la crÃ©ation du stage");
    }
  };

  return (
    <div className="stages-container">
      <div className="stages-header">
        <h2>Mes Offres de Stage</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(true)}
        >
          Ajouter une offre
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {showForm && (
        <div className="stage-form">
          <h3>Nouvelle offre de stage</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Titre</label>
              <input
                type="text"
                className="form-control"
                value={formData.titre}
                onChange={e => setFormData({...formData, titre: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>Description</label>
              <textarea
                className="form-control"
                value={formData.description}
                onChange={e => setFormData({...formData, description: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>DurÃ©e</label>
              <input
                type="text"
                className="form-control"
                value={formData.duree}
                onChange={e => setFormData({...formData, duree: e.target.value})}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Date de dÃ©but</label>
                <input
                  type="date"
                  className="form-control"
                  value={formData.dateDebut}
                  onChange={e => setFormData({...formData, dateDebut: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label>Date de fin</label>
                <input
                  type="date"
                  className="form-control"
                  value={formData.dateFin}
                  onChange={e => setFormData({...formData, dateFin: e.target.value})}
                  required
                />
              </div>
            </div>

            <div className="form-actions">
              <button type="submit" className="btn btn-primary">CrÃ©er</button>
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => setShowForm(false)}
              >
                Annuler
              </button>
            </div>
          </form>
        </div>
      )}

      {isLoading ? (
        <div className="loading">Chargement...</div>
      ) : (
        <div className="stages-grid">
          {stages.map(stage => (
            <div key={stage.id} className="stage-card">
              <h3>{stage.titre}</h3>
              <div className="stage-details">
                <p><strong>DurÃ©e:</strong> {stage.duree}</p>
                <p><strong>PÃ©riode:</strong> {new Date(stage.dateDebut).toLocaleDateString()} - {new Date(stage.dateFin).toLocaleDateString()}</p>
                <p className="description">{stage.description}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EntrepriseStages;
