import React, { useEffect, useState } from 'react';
import { fetchEntrepriseCandidatures, updateCandidatureStatus } from '../services/api';

const EntrepriseCandidatures = () => {
    const [candidatures, setCandidatures] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchEntrepriseCandidatures()
            .then(setCandidatures)
            .catch(err => setError(err.response?.data?.error || 'Erreur de chargement'));
    }, []);

    const handleUpdate = async (id, status) => {
        try {
            await updateCandidatureStatus(id, { status });
            setCandidatures(candidatures.map(c => c.id === id ? { ...c, status } : c));
        } catch (err) {
            setError('Erreur lors de la mise à jour');
        }
    };

    return (
        <div>
            <h2>Candidatures reçues</h2>
            {error && <div className="error">{error}</div>}
            {candidatures.map(c => (
                <div className="card" key={c.id}>
                    <div><b>Titre:</b> {c.Stage?.titre}</div>
                    <div><b>Status:</b> {c.status}</div>
                    <button onClick={() => handleUpdate(c.id, 'valide')}>Accepter</button>
                    <button onClick={() => handleUpdate(c.id, 'refuse')}>Refuser</button>
                </div>
            ))}
        </div>
    );
};

export default EntrepriseCandidatures;
