import React, { useState, useEffect } from 'react';
import { authService } from '../services/authService';
import '../styles/Profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [editMode, setEditMode] = useState(false);
    const [message, setMessage] = useState('');    const [formData, setFormData] = useState({
        nom: '',
        prenom: '',
        email: '',
        adresse: '',
        dateNaissance: '',
        cv: null
    });

    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('user'));
        if (userData) {
            setUser(userData);
            setFormData({
                nom: userData.nom || '',
                prenom: userData.prenom || '',
                email: userData.email || '',
                telephone: userData.telephone || '',
                adresse: userData.adresse || '',
                dateNaissance: userData.dateNaissance ? userData.dateNaissance.split('T')[0] : '',
                cv: null
            });
        }
        setLoading(false);
    }, []);

    const handleInputChange = (e) => {
        const { name, value, files } = e.target;
        if (files) {
            setFormData(prev => ({ ...prev, [name]: files[0] }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        
        try {
            const formDataToSend = new FormData();
            Object.keys(formData).forEach(key => {
                if (formData[key] !== null && formData[key] !== '') {
                    formDataToSend.append(key, formData[key]);
                }
            });

            const response = await authService.updateProfile(formDataToSend);
            
            // Mettre √† jour le localStorage avec les nouvelles donn√©es
            const updatedUser = { ...user, ...response.data };
            localStorage.setItem('user', JSON.stringify(updatedUser));
            setUser(updatedUser);
            
            setMessage('Profil mis √† jour avec succ√®s !');
            setEditMode(false);
            
            setTimeout(() => setMessage(''), 3000);
        } catch (error) {
            setMessage('Erreur lors de la mise √† jour du profil: ' + (error.response?.data?.message || error.message));
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="profile-container">
                <div className="loading-spinner">
                    <div className="spinner"></div>
                    <p>Chargement...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="profile-container">
            <div className="profile-header">
                <div className="profile-avatar">
                    <span className="avatar-icon">üë§</span>
                </div>
                <div className="profile-title">
                    <h1>Mon Profil</h1>
                    <p>G√©rez vos informations personnelles</p>
                </div>
                <button 
                    className={`edit-toggle-btn ${editMode ? 'save' : 'edit'}`}
                    onClick={() => setEditMode(!editMode)}
                >
                    {editMode ? '‚ùå Annuler' : '‚úèÔ∏è Modifier'}
                </button>
            </div>

            {message && (
                <div className={`message ${message.includes('succ√®s') ? 'success' : 'error'}`}>
                    {message}
                </div>
            )}

            <div className="profile-content">
                {!editMode ? (
                    <div className="profile-view">
                        <div className="info-section">
                            <h3>üìã Informations Personnelles</h3>
                            <div className="info-grid">
                                <div className="info-item">
                                    <label>Nom</label>
                                    <span>{user?.nom || 'Non renseign√©'}</span>
                                </div>
                                <div className="info-item">
                                    <label>Pr√©nom</label>
                                    <span>{user?.prenom || 'Non renseign√©'}</span>
                                </div>
                                <div className="info-item">
                                    <label>Email</label>
                                    <span>{user?.email}</span>
                                </div>
                                <div className="info-item">
                                    <label>T√©l√©phone</label>
                                    <span>{user?.telephone || 'Non renseign√©'}</span>
                                </div>
                                <div className="info-item">
                                    <label>Adresse</label>
                                    <span>{user?.adresse || 'Non renseign√©e'}</span>
                                </div>
                                <div className="info-item">
                                    <label>Date de naissance</label>
                                    <span>
                                        {user?.dateNaissance 
                                            ? new Date(user.dateNaissance).toLocaleDateString('fr-FR')
                                            : 'Non renseign√©e'
                                        }
                                    </span>
                                </div>
                            </div>
                        </div>

                        <div className="info-section">
                            <h3>üéØ Informations de Compte</h3>
                            <div className="info-grid">
                                <div className="info-item">
                                    <label>R√¥le</label>
                                    <span className="role-badge">{user?.role}</span>
                                </div>
                                <div className="info-item">
                                    <label>Date de cr√©ation</label>
                                    <span>
                                        {user?.createdAt 
                                            ? new Date(user.createdAt).toLocaleDateString('fr-FR')
                                            : 'Non disponible'
                                        }
                                    </span>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : (
                    <form onSubmit={handleSubmit} className="profile-edit-form">
                        <div className="form-section">
                            <h3>üìù Modifier les Informations</h3>
                            <div className="form-grid">
                                <div className="form-group">
                                    <label htmlFor="nom">Nom *</label>
                                    <input
                                        type="text"
                                        id="nom"
                                        name="nom"
                                        value={formData.nom}
                                        onChange={handleInputChange}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="prenom">Pr√©nom *</label>
                                    <input
                                        type="text"
                                        id="prenom"
                                        name="prenom"
                                        value={formData.prenom}
                                        onChange={handleInputChange}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="email">Email *</label>
                                    <input
                                        type="email"
                                        id="email"
                                        name="email"
                                        value={formData.email}
                                        onChange={handleInputChange}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="telephone">T√©l√©phone</label>
                                    <input
                                        type="tel"
                                        id="telephone"
                                        name="telephone"
                                        value={formData.telephone}
                                        onChange={handleInputChange}
                                    />
                                </div>
                                <div className="form-group full-width">
                                    <label htmlFor="adresse">Adresse</label>
                                    <input
                                        type="text"
                                        id="adresse"
                                        name="adresse"
                                        value={formData.adresse}
                                        onChange={handleInputChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="dateNaissance">Date de naissance</label>
                                    <input
                                        type="date"
                                        id="dateNaissance"
                                        name="dateNaissance"
                                        value={formData.dateNaissance}
                                        onChange={handleInputChange}
                                    />
                                </div>
                                {user?.role === 'etudiant' && (
                                    <div className="form-group">
                                        <label htmlFor="cv">CV (PDF)</label>
                                        <input
                                            type="file"
                                            id="cv"
                                            name="cv"
                                            accept=".pdf"
                                            onChange={handleInputChange}
                                        />
                                        <small>Formats accept√©s: PDF uniquement</small>
                                    </div>
                                )}
                            </div>
                        </div>

                        <div className="form-actions">
                            <button type="submit" className="btn btn-primary" disabled={loading}>
                                {loading ? 'Enregistrement...' : 'üíæ Enregistrer les modifications'}
                            </button>
                            <button 
                                type="button" 
                                className="btn btn-secondary"
                                onClick={() => setEditMode(false)}
                            >
                                Annuler
                            </button>
                        </div>
                    </form>
                )}
            </div>
        </div>
    );
};

export default Profile;
