import React, { useState } from 'react';
import { createStage } from '../services/api';

const SubmitStage = () => {
    const [form, setForm] = useState({
        titre: '',
        description: '',
        dateDebut: '',
        dateFin: '',
        tuteurId: '',
        entrepriseId: '',
    });
    const [success, setSuccess] = useState('');
    const [error, setError] = useState('');

    const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async e => {
        e.preventDefault();
        setError('');
        setSuccess('');
        try {
            await submitStageRequest(form);
            setSuccess('Demande de stage soumise !');
            setForm({ titre: '', description: '', dateDebut: '', dateFin: '', tuteurId: '', entrepriseId: '' });
        } catch (err) {
            setError(err.response?.data?.error || 'Erreur lors de la soumission.');
        }
    };

    return (
        <div className="card">
            <h2>Soumettre une demande de stage</h2>
            {success && <div className="success">{success}</div>}
            {error && <div className="error">{error}</div>}
            <form onSubmit={handleSubmit}>
                <input name="titre" placeholder="Titre" value={form.titre} onChange={handleChange} required />
                <input name="description" placeholder="Description" value={form.description} onChange={handleChange} required />
                <input name="dateDebut" type="date" value={form.dateDebut} onChange={handleChange} required />
                <input name="dateFin" type="date" value={form.dateFin} onChange={handleChange} required />
                <input name="tuteurId" placeholder="ID du tuteur" value={form.tuteurId} onChange={handleChange} />
                <input name="entrepriseId" placeholder="ID de l'entreprise" value={form.entrepriseId} onChange={handleChange} />
                <button type="submit">Soumettre</button>
            </form>
        </div>
    );
};

export default SubmitStage;
