import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:5000/api',
});

api.interceptors.request.use(
    config => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers['Authorization'] = `Bearer ${token}`;
        }
        return config;
    },
    error => Promise.reject(error)
);

export const registerUser = async (userData) => {
    const response = await api.post('/auth/register', userData);
    return response.data;
};

export const loginUser = async (credentials) => {
    const response = await api.post('/auth/login', credentials);
    if (response.data.user) {
        localStorage.setItem('user', JSON.stringify(response.data.user));
    }
    return response.data;
};

export const fetchInternships = async () => {
    const response = await api.get('/stages');
    return response.data;
};

export const createInternship = async (internshipData) => {
    const response = await api.post('/stages', internshipData);
    return response.data;
};
export const submitStageRequest = async (data) => {
    const response = await api.post('/etudiant/stage-request', data);
    return response.data;
};

export const fetchMyCandidatures = async () => {
    const response = await api.get('/etudiant/candidatures');
    return response.data;
};

export const fetchEntrepriseCandidatures = async () => {
    const response = await api.get('/entreprise/candidatures');
    return response.data;
};

export const updateCandidatureStatus = async (id, data) => {
    const response = await api.put(`/entreprise/candidatures/${id}`, data);
    return response.data;
};

export const fetchUsers = async () => {
    const response = await api.get('/admin/users');
    return response.data;
};

export const toggleUserActivation = async (id) => {
    const response = await api.put(`/admin/users/${id}/toggle`);
    return response.data;
};

export const fetchStats = async () => {
    const response = await api.get('/admin/stats');
    return response.data;
};
export default api;
