import axios from 'axios';

const API_URL = 'http://localhost:8080/api';

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add request interceptor for auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 404) {
      console.error('API endpoint not found:', error.config.url);
    }
    return Promise.reject(error);
  }
);

// Auth endpoints
export const registerUser = (data) => api.post('/auth/register', data);
export const loginUser = async (credentials) => {
  try {
    const response = await api.post('/auth/login', credentials);

    // Validate response structure
    const { data } = response;
    if (!data || !data.accessToken || !data.role) {
      throw new Error('Invalid response format from server');
    }

    return response;
  } catch (error) {
    console.error('Login API Error:', {
      status: error.response?.status,
      message: error.response?.data?.message || error.message
    });
    throw error;
  }
};

// Admin endpoints
export const registerAdmin = async (adminData) => {
    try {
        // Creating the user object according to the User model
        const userData = {
            nom: adminData.nom,
            prenom: adminData.prenom,
            email: adminData.email,
            motdepasse: adminData.motdepasse,
            role: 'admin',
            dateInscription: new Date().toISOString(),
            actif: true,
            adminCode: adminData.adminCode // Code for verification
        };

        const response = await api.post('/auth/admin/register', userData);

        // Validate response structure
        const { data } = response;
        if (!data) {
            throw new Error("Aucune donnée reçue du serveur");
        }

        return {
            success: true,
            data: {
                id: data.id,
                nom: data.nom,
                prenom: data.prenom,
                email: data.email,
                role: data.role,
                dateInscription: data.dateInscription,
                actif: data.actif
            },
            message: "Inscription administrateur réussie"
        };

    } catch (error) {
        console.error("Admin registration error:", {
            status: error.response?.status,
            message: error.response?.data?.message || "Erreur lors de l'inscription administrateur",
            details: error.response?.data
        });
        throw error;
    }
};

// User management endpoints
export const getUsers = async () => {
    const response = await api.get('/admin/users');
    return response.data;
};

export const getUserById = async (userId) => {
    const response = await api.get(`/admin/users/${userId}`);
    return response.data;
};

// Combine the duplicate updateUserStatus into one function
export const updateUserStatus = async (userId, data) => {
    try {
        const response = await api.put(`/admin/users/${userId}/status`, data);
        return response.data;
    } catch (error) {
        console.error('Update user status error:', error);
        throw error;
    }
};

// Statistics endpoints
export const getSystemStats = async () => {
    const response = await api.get('/admin/stats');
    return response.data;
};

export const getInternshipStats = async () => {
    const response = await api.get('/admin/stages/stats');
    return response.data;
};

export const getApplicationStats = async () => {
