import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { login } from '../../services/authService';
import leftImage from '../../assets/im1.png';
import rightImage from '../../assets/im2.png';

const Login = () => {
  const [form, setForm] = useState({ email: '', motdepasse: '' });
  const [error, setError] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(form);

      if (response.data) {
        // First check if we have the necessary data
        const { id, nom, email, role, accessToken } = response.data;

        if (!role || !accessToken) {
          throw new Error('Données d\'authentification invalides');
        }

        // Store user data and token
        localStorage.setItem('token', accessToken);
        localStorage.setItem('user', JSON.stringify({
          id,
          nom,
          email,
          role,
        }));

        // Force page reload and redirect based on role
        switch (role) {
          case 'entreprise':
            window.location.href = '/entreprise-dashboard';
            break;
          case 'etudiant':
            window.location.href = '/dashboard';
            break;
          default:
            throw new Error('Role non reconnu');
        }
      }
    } catch (err) {
      console.error('Login error:', err);
      setError(
        err.response?.data?.message || 
        err.message || 
        "Email ou mot de passe incorrect"
      );
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-container">
        <div className="login-images">
          <div className="image-left">
            <img src={leftImage} alt="Welcome" />
          </div>
          <div className="image-right">
            <img src={rightImage} alt="Features" />
          </div>
        </div>

        <div className="login-box">
          <div className="login-form-container">
            <h2>Bienvenue</h2>
            <p className="login-subtitle">
              Connectez-vous pour continuer
            </p>

            {error && <div className="error-message">{error}</div>}

            <form onSubmit={handleSubmit} className="login-form">
              <div className="form-group">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  value={form.email}
                  onChange={(e) => setForm({...form, email: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Mot de passe</label>
                <input
                  type="password"
                  className="form-control"
                  value={form.motdepasse}
                  onChange={(e) => setForm({...form, motdepasse: e.target.value})}
                  required
                />
              </div>

              <button type="submit" className="btn btn-primary btn-block">
                Se connecter
              </button>
            </form>

            <div className="login-footer">
              <p>Pas encore de compte ?</p>
              <Link to="/register" className="btn btn-secondary btn-block">
                Créer un compte
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
