import React, { useState, useEffect } from 'react';
import { getAllStages } from '../services/api';

const InternshipList = () => {
  const [stages, setStages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStages = async () => {
      try {
        const data = await getAllStages();
        console.log('Stages data:', data); // Debug log
        setStages(data);
      } catch (err) {
        console.error('Error fetching stages:', err);
        setError("Erreur lors du chargement des stages");
      } finally {
        setIsLoading(false);
      }
    };

    fetchStages();
  }, []);

  if (isLoading) {
    return <div className="loading">Chargement des stages...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="internship-list">
      <h2>Stages Disponibles</h2>
      <div className="internships-grid">
        {stages.length === 0 ? (
          <p>Aucun stage disponible pour le moment.</p>
        ) : (
          stages.map(stage => (
            <div key={stage.id} className="internship-card">
              <h3>{stage.titre}</h3>
              <div className="internship-details">
                <p><strong>Entreprise:</strong> {stage.entreprise}</p>
                <p><strong>Dur√©e:</strong> {stage.duree}</p>
                <p><strong>Description:</strong></p>
                <p>{stage.description}</p>
              </div>
              <button 
                className="btn btn-primary"
                onClick={() => {/* TODO: Handle application */}}
              >
                Postuler
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default InternshipList;
