  const history = useHistory();

  const handleRoleSelect = (role) => {
    if (role === 'admin') {
      history.push('/admin-register');
    } else {
      history.push('/login', { selectedRole: role });
    }
  };

  return (
    <div className="home-page">
      <div className="home-container">
        <div className="hero-section">
          <h1>Bienvenue sur InternMatch</h1>
          <p className="hero-text">
            La plateforme qui connecte les √©tudiants, les entreprises et les administrateurs 
            pour une gestion simplifi√©e des stages.
          </p>
        </div>

        <div className="cards-container">
          <div className="role-card" onClick={() => handleRoleSelect('etudiant')}>
            <div className="role-icon">üë®‚Äçüéì</div>
            <h3>√âtudiant</h3>
            <p>Trouvez le stage parfait et g√©rez vos candidatures facilement</p>
            <button className="btn btn-primary">Commencer</button>
          </div>

          <div className="role-card" onClick={() => handleRoleSelect('entreprise')}>
            <div className="role-icon">üè¢</div>
            <h3>Entreprise</h3>
            <p>Publiez des offres et trouvez les meilleurs talents</p>
            <button className="btn btn-primary">Commencer</button>
          </div>

          <div className="role-card" onClick={() => handleRoleSelect('admin')}>
            <div className="role-icon">‚öôÔ∏è</div>
            <h3>Administrateur</h3>
            <p>Supervisez et g√©rez l'ensemble du processus de stage</p>
