import React, { useState, useEffect } from 'react';
import { getAllStages, postulerStage } from '../services/api';

const InternshipList = () => {
  const [stages, setStages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchStages();
  }, []);

  const fetchStages = async () => {
    setIsLoading(true);
    try {
      const data = await getAllStages();
      setStages(data);
    } catch (err) {
      setError("Erreur lors du chargement des stages");
    } finally {
      setIsLoading(false);
    }
  };

  const handlePostuler = async (stageId) => {
    try {
      await postulerStage({ stageId });
      setSuccessMessage('Candidature envoyée avec succès!');
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError("Erreur lors de l'envoi de la candidature");
    }
  };

  return (
    <div className="internship-list">
      <h2>Stages Disponibles</h2>

      {successMessage && <div className="success-message">{successMessage}</div>}
      {error && <div className="error-message">{error}</div>}

      {isLoading ? (
        <div className="loading">Chargement...</div>
      ) : (
        <div className="internships-grid">
          {stages.map(stage => (
            <div key={stage.id} className="internship-card">
              <h3>{stage.titre}</h3>
              <div className="internship-details">
                <p><strong>Entreprise:</strong> {stage.entrepriseNom}</p>
                <p><strong>Durée:</strong> {stage.duree}</p>
                <p className="description">{stage.description}</p>
              </div>
              <button 
                className="btn btn-primary"
                onClick={() => handlePostuler(stage.id)}
              >
                Postuler
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default InternshipList;
