import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { registerAdmin } from '../../services/api';

const AdminRegister = () => {
  const [form, setForm] = useState({
    nom: '',
    prenom: '',
    email: '',
    motdepasse: '',
    role: 'admin',
    adminCode: '' // Required for admin registration
  });
  const [error, setError] = useState('');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await registerAdmin(form);
      history.push('/admin-dashboard'); // Redirect to admin dashboard after registration
    } catch (err) {
      setError(err.response?.data?.message || "Erreur lors de l'inscription administrateur");
    }
  };

  return (
    <div className="admin-register-wrapper">
      <div className="admin-register-container">
        <div className="admin-register-box">
          <h2>Inscription Administrateur</h2>
          <p className="admin-register-subtitle">
            Créer un nouveau compte administrateur
          </p>

          {error && <div className="error-message">{error}</div>}

          <form onSubmit={handleSubmit} className="admin-register-form">
            <div className="form-row">
              <div className="form-group">
                <label>Nom</label>
                <input
                  type="text"
                  className="form-control"
                  value={form.nom}
                  onChange={(e) => setForm({...form, nom: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label>Prénom</label>
                <input
                  type="text"
                  className="form-control"
                  value={form.prenom}
                  onChange={(e) => setForm({...form, prenom: e.target.value})}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                className="form-control"
                value={form.email}
                onChange={(e) => setForm({...form, email: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>Mot de passe</label>
              <input
                type="password"
                className="form-control"
                value={form.motdepasse}
                onChange={(e) => setForm({...form, motdepasse: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>Code Administrateur</label>
              <input
                type="password"
                className="form-control"
                value={form.adminCode}
                onChange={(e) => setForm({...form, adminCode: e.target.value})}
                required
              />
            </div>

            <button type="submit" className="btn btn-primary btn-block">
              Créer le compte administrateur
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AdminRegister;
