import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { registerUser } from '../../services/api';
import leftImage from '../../assets/im1.png';
import rightImage from '../../assets/im2.png';

const Register = () => {
  const [form, setForm] = useState({
    nom: '',
    prenom: '',
    email: '',
    motdepasse: '',
    role: 'etudiant',
    // Additional fields for specific roles
    niveau: '',
    filiere: '',
    secteur: '',
    adresse: '',
    adminCode: '' // For admin verification
  });

  const [error, setError] = useState('');
  const history = useHistory();

  // Conditional rendering of additional fields based on role
  const renderRoleSpecificFields = () => {
    switch(form.role) {
      case 'etudiant':
        return (
          <>
            <div className="form-group">
              <label className="form-label">Niveau d'études</label>
              <input
                type="text"
                className="form-control"
                value={form.niveau}
                onChange={(e) => setForm({...form, niveau: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Filière</label>
              <input
                type="text"
                className="form-control"
                value={form.filiere}
                onChange={(e) => setForm({...form, filiere: e.target.value})}
                required
              />
            </div>
          </>
        );
      case 'entreprise':
        return (
          <>
            <div className="form-group">
              <label className="form-label">Secteur d'activité</label>
              <input
                type="text"
                className="form-control"
                value={form.secteur}
                onChange={(e) => setForm({...form, secteur: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Adresse</label>
              <input
                type="text"
                className="form-control"
                value={form.adresse}
                onChange={(e) => setForm({...form, adresse: e.target.value})}
                required
              />
            </div>
          </>
        );
      case 'admin':
        return (
          <div className="form-group">
            <label className="form-label">Code administrateur</label>
            <input
              type="password"
              className="form-control"
              value={form.adminCode}
              onChange={(e) => setForm({...form, adminCode: e.target.value})}
              required
            />
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="register-wrapper">
      <div className="register-container">
        <div className="login-images">
          <div className="image-left">
            <img src={leftImage} alt="Welcome" />
          </div>
          <div className="image-right">
            <img src={rightImage} alt="Features" />
          </div>
        </div>

        <div className="register-box">
          <div className="register-form-container">
            <h2>Créer un compte</h2>
            <p className="register-subtitle">
              Rejoignez InternMatch dès aujourd'hui
            </p>

            {error && <div className="error-message">{error}</div>}

            <form onSubmit={handleSubmit} className="register-form">
              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Nom</label>
                  <input
                    type="text"
                    className="form-control"
                    value={form.nom}
                    onChange={(e) => setForm({...form, nom: e.target.value})}
                    required
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Prénom</label>
                  <input
                    type="text"
                    className="form-control"
                    value={form.prenom}
                    onChange={(e) => setForm({...form, prenom: e.target.value})}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Email</label>
                <input
                  type="email"
                  className="form-control"
                  value={form.email}
                  onChange={(e) => setForm({...form, email: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Mot de passe</label>
                <input
                  type="password"
                  className="form-control"
                  value={form.motdepasse}
                  onChange={(e) => setForm({...form, motdepasse: e.target.value})}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Type de compte</label>
                <select
                  className="form-control"
                  value={form.role}
                  onChange={(e) => setForm({...form, role: e.target.value})}
                >
                  <option value="etudiant">Étudiant</option>
                  <option value="entreprise">Entreprise</option>
                  <option value="admin">Administrateur</option>
                </select>
              </div>

              {renderRoleSpecificFields()}

              <button type="submit" className="btn btn-primary btn-block">
                S'inscrire
              </button>
            </form>

            <div className="register-footer">
              <p>Déjà membre ?</p>
              <Link to="/login" className="btn btn-secondary btn-block">
                Se connecter
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
