import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import Logo from '../Logo/Logo'; // Updated import path

const NavBar = ({ isAuthenticated, user }) => {
  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/login';
  };

  return (
    <nav className={`navbar ${isAuthenticated ? 'authenticated' : ''}`}>
      <div className="nav-content">
        <div className="nav-left">
          <Link to="/" className="logo-link">
            <Logo width="120px" />
          </Link>
        </div>

        {isAuthenticated ? (
          <div className="nav-menu">
            {user?.role === 'entreprise' && (
              <>
                {/* Simplified enterprise menu */}
                <Link to="/entreprise-candidatures" className="nav-link">
                  Demandes Reçues
                </Link>
              </>
            )}
            
            {/* Keep other role menus */}
            {user?.role === 'etudiant' && (
              <Link to="/mes-candidatures" className="nav-link">Mes candidatures</Link>
            )}
            
            {user?.role === 'admin' && (
              <>
                <Link to="/admin-users" className="nav-link">Utilisateurs</Link>
                <Link to="/admin-stats" className="nav-link">Statistiques</Link>
              </>
            )}

            <button onClick={handleLogout} className="btn btn-secondary">
              Déconnexion
            </button>
          </div>
        ) : (
          <div className="nav-right">
            {/* Only show regular register for non-admin routes */}
            {window.location.pathname !== '/admin-register' && (
              <Link to="/register" className="btn btn-secondary">S'inscrire</Link>
            )}
            <Link to="/login" className="btn btn-primary">Se connecter</Link>
          </div>
        )}
      </div>
    </nav>
  );
};

export default NavBar;
